import React, { useEffect } from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import logo from './assets/logo.svg'
import { useFonts } from '@use-expo/font';
import { AppLoading } from 'expo';

export default function App() {
  let [fontsLoaded] = useFonts({
    'ReemKufi-Regular': require('./assets/fonts/ReemKufi-Regular.ttf'),
  });

  if (!fontsLoaded) {
    return <AppLoading />;
  } else {
    return (
      <LinearGradient colors={["rgba(0,0,0,0.98)", "#4e4e4e", "rgba(0,0,0,0.99)"]} style={styles.background}>
        <View style={{ display: 'flex', flexDirection: 'row', margin: 'auto', alignItems: 'center' }}>
          <Image source={logo} style={styles.logo} />
          <Text style={styles.appTitle}>SCOUT</Text>
        </View>
      </LinearGradient>
    );
  }
}

const styles = StyleSheet.create({
  background: {
    height: '100vh',
    backgroundColor: 'white',
  },

  logo: {
    width: '50px',
    height: '77.04px',
    marginRight: '11px',
  },

  appTitle: {
    color: 'white',
    fontSize: 72,
    fontFamily: 'ReemKufi-Regular',
  },
});
