import React, { useEffect } from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import logo from './assets/logo.svg'
import { useFonts } from '@use-expo/font';
import { AppLoading } from 'expo';

export default function App() {
  let [fontsLoaded] = useFonts({
    'ReemKufi-Regular': require('./assets/fonts/ReemKufi-Regular.ttf'),
  });

  if (!fontsLoaded) {
    return <AppLoading />;
  } else {
    return (
      <LinearGradient colors={["rgba(0,0,0,0.95)", "#4e4e4e", "rgba(0,0,0,0.95)"]} style={styles.background}>
        <View style={{ display: 'flex', flexDirection: 'row', margin: 'auto' }}>
          <Image source={logo} style={{ width: '45px', height: '77.04px' }} />
          <Text style={{ color: 'white', fontSize: 40, fontFamily: 'ReemKufi-Regular' }}>SCOUT</Text>
        </View>
      </LinearGradient>
    );
  }
}

const styles = StyleSheet.create({
  background: {
    height: '100vh',
    backgroundColor: 'white',
  },

  logo: {
    width: "13%",
    flex: 1,
    marginLeft: '10%',
  },

  appTitle: {
    font: "R"
  },
});
